  { json: { day: 12, concept: "Organization policies, folders, constraints, policy hierarchy", resources: [
    { text: "Org Policy overview", url: "https://cloud.google.com/resource-manager/docs/organization-policy/overview" }
  ]}},
  { json: { day: 13, concept: "Security services: Cloud KMS, CMEK/CMK, Secret Manager", resources: [
    { text: "Cloud KMS", url: "https://cloud.google.com/kms/docs" },
    { text: "Secret Manager", url: "https://cloud.google.com/secret-manager/docs" },
    { text: "CMEK on GCP", url: "https://cloud.google.com/compute/docs/disks/customer-managed-encryption" }
  ]}},
  { json: { day: 14, concept: "Perimeter security: VPC Service Controls, DLP overview", resources: [
    { text: "VPC Service Controls", url: "https://cloud.google.com/vpc-service-controls/docs" },
    { text: "Cloud DLP", url: "https://cloud.google.com/dlp/docs" }
  ]}},
  { json: { day: 15, concept: "Analytics design: BigQuery storage/compute separation, partitions, slots", resources: [
    { text: "BigQuery architecture", url: "https://cloud.google.com/bigquery/docs/introduction" },
    { text: "Partitions & clustering", url: "https://cloud.google.com/bigquery/docs/partitioned-tables" }
  ]}},
  { json: { day: 16, concept: "Data pipelines: Pub/Sub ‚Üí Dataflow ‚Üí BigQuery patterns", resources: [
    { text: "Dataflow (Apache Beam)", url: "https://cloud.google.com/dataflow/docs" },
    { text: "Streaming design", url: "https://cloud.google.com/architecture/streaming-analytics" }
  ]}},
  { json: { day: 17, concept: "Ops: Cloud Monitoring, Logging, alerting, SLOs", resources: [
    { text: "Cloud Monitoring", url: "https://cloud.google.com/monitoring/docs" },
    { text: "Cloud Logging", url: "https://cloud.google.com/logging/docs" },
    { text: "SLOs on GCP", url: "https://cloud.google.com/stackdriver/docs/solutions/slo-monitoring" }
  ]}},
  { json: { day: 18, concept: "Reliability & scaling: autoscaling patterns, regional vs zonal design", resources: [
    { text: "Managed instance groups autoscaling", url: "https://cloud.google.com/compute/docs/autoscaler" },
    { text: "Regional architecture guidance", url: "https://cloud.google.com/architecture/framework" }
  ]}},
  { json: { day: 19, concept: "Cost management: billing setup, budgets, CUDs/committed use, rightsizing", resources: [
    { text: "Cost management", url: "https://cloud.google.com/billing/docs/how-to/cost-management" },
    { text: "Budgets & alerts", url: "https://cloud.google.com/billing/docs/how-to/budgets" }
  ]}},
  { json: { day: 20, concept: "HA & DR: RTO/RPO, multi-region design, backup & restore patterns", resources: [
    { text: "DR planning on GCP", url: "https://cloud.google.com/architecture/disaster-recovery-planning-guide" }
  ]}},
  { json: { day: 21, concept: "CI/CD: Cloud Build, Artifact Registry, Cloud Deploy", resources: [
    { text: "Cloud Build", url: "https://cloud.google.com/build/docs/overview" },
    { text: "Cloud Deploy", url: "https://cloud.google.com/deploy/docs/overview" }
  ]}},
  { json: { day: 22, concept: "Infrastructure as Code: Terraform on GCP, policy as code basics", resources: [
    { text: "Terraform on GCP", url: "https://cloud.google.com/docs/terraform" },
    { text: "Config Validator (policy as code)", url: "https://cloud.google.com/anthos-config-management/docs/concepts/policy-controller" }
